use rcbill_my;

drop table if exists rcbill_my.holidays;

CREATE TABLE `holidays` (
	`HOLIDAY_ID` INT(11) NOT NULL,
	`HOLIDAY_DATE` DATETIME NULL DEFAULT NULL,
	`WEEK_DAY` VARCHAR(12) NULL DEFAULT NULL,
	`HOLIDAY_NAME` VARCHAR(45) NULL DEFAULT NULL,
	`COUNTRY_CODE` VARCHAR(45) NOT NULL DEFAULT 'ALL',
	PRIMARY KEY (`HOLIDAY_ID`)
)
ENGINE=InnoDB DEFAULT CHARSET UTF8
;


select * from rcbill_my.holidays;

